// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract MediChain {
    // State Variables
    string public name;
    // uint public countPatients;
    // uint public countDoctors;
    // uint public countInsurer;
    // uint public countPolicy;
    address[] public patientList;
    address[] public doctorList;
    address[] public insurerList;
    Policy[] public policyList;
    uint public claimsCount;
    uint public transactionCount;
    mapping (uint => Claims) public claims;
    mapping (uint => Transactions) public transactions;
    mapping (address => Patient) public patientInfo;
    mapping (address => Doctor) public doctorInfo;
    mapping (address => Insurer) public insurerInfo;
    mapping (string => address) public emailToAddress;
    mapping (string => uint) public emailToDesignation;

    struct Patient {
        string name;
        string email;
        uint age;
        string record;
        bool exists;
        bool policyActive;
        Policy policy;
        uint[] transactions;
        address[] doctorAccessList;
    }
    struct Doctor {
        string name;
        string email;
        bool exists;
        uint[] transactions;
        address[] patientAccessList;
    }
    struct Insurer {
        string name;
        string email;
        bool exists;
        Policy[] policies;
        address[] patients;
        uint[] claims;
        uint[] transactions;
    }
    struct Policy {
        uint id;
        address insurer;
        string name;
        uint coverValue;
        uint timePeriod;
        uint premium;
    }
    struct Claims {
        address doctor;
        address patient;
        address insurer;
        string policyName;
        string record;
        uint valueClaimed;
        bool approved;
        bool rejected;
        uint transactionId;
    }
    struct Transactions {
        address sender;
        address receiver;
        uint value;
        bool settled;
    }

    

    

    

    constructor(){
        name = "medichain";
        claimsCount = 0;
        transactionCount = 0;
    }

    function register(string memory _name, uint _age, uint _designation, string memory _email, string memory _hash) public {
        require(msg.sender != address(0));
        require(bytes(_name).length > 0);
        require(bytes(_email).length > 0);
        require(emailToAddress[_email] == address(0));
        require(emailToDesignation[_email] == 0);
        address _addr = msg.sender;
        require(!patientInfo[_addr].exists);
        require(!doctorInfo[_addr].exists);
        require(!insurerInfo[_addr].exists);
        if(_designation == 1){
        require(_age > 0);
        require(bytes(_hash).length > 0);
        Patient storage pinfo = patientInfo[msg.sender];  // Corrected line
        pinfo.name = _name;
        pinfo.email = _email;
        pinfo.age = _age;
        pinfo.record = _hash;
        pinfo.exists = true;
        pinfo.doctorAccessList;
        patientList.push(msg.sender);  // Corrected line
        emailToAddress[_email] = msg.sender;  // Corrected line
        emailToDesignation[_email] = _designation;
    }
        else if (_designation == 2){
            Doctor storage dinfo = doctorInfo[_addr];
            dinfo.name = _name;
            dinfo.email = _email;
            dinfo.exists = true;
            doctorList.push(_addr);
            emailToAddress[_email] = _addr;
            emailToDesignation[_email] = _designation;
        }
        else if(_designation == 3){
            Insurer storage iinfo = insurerInfo[_addr];
            iinfo.name = _name;
            iinfo.email = _email;
            iinfo.exists = true;
            insurerList.push(_addr);
            emailToAddress[_email] = _addr;
            emailToDesignation[_email] = _designation;
        }
        else{
            revert();
        }
    }

    function login(address _addr) view public returns (uint){
        require(_addr != address(0));
        if(patientInfo[_addr].exists){
            return 1;
        }else if(doctorInfo[_addr].exists){
            return 2;
        }else if(insurerInfo[_addr].exists){
            return 3;
        }else{
            return 0;
        }
    }


